import CommentList from './CommentList'
import AddComment from './AddComment'
import Loading from './Loading'
import Error from './Error'
import { useState, useEffect } from 'react'

const CommentArea = ({ asin }) => {
  const [comments, setComments] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [isError, setIsError] = useState(false)

  useEffect(() => {
    const fetchComments = async (a) => {
      try {
        let response = await fetch(
          'https://striveschool-api.herokuapp.com/api/comments/' + a,
          {
            headers: {
              Authorization:
                'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MjI4YjA3YTRlYTdiMTAwMTVkMDY3YWIiLCJpYXQiOjE2NDgwNjcwMzksImV4cCI6MTY0OTI3NjYzOX0.sBKkw7wQQ_5fkciKFIJTia-jJYnPmm39eRgUh7lSF24',
            },
          },
        )
        console.log(response)
        if (response.ok) {
          let comments = await response.json()
          setComments(comments)
        } else {
          console.log('error')
        }
      } catch (error) {
        console.log(error)
        setIsError(true)
      }
    }
    fetchComments(asin)
  }, [asin])

  return (
    <div>
      {isLoading && <Loading />}
      {isError && <Error />}
      <AddComment asin={asin} />
      <CommentList commentsToShow={comments} />
    </div>
  )
}

export default CommentArea
